"use strict";

var $navMobile;
var $navBtn;
var $footerYear;
var $navLink;

var prepareDOMElements = function prepareDOMElements() {
  $navMobile = document.querySelector(".nav-mobile");
  $navBtn = document.querySelector(".hamburger");
  $footerYear = document.querySelector(".footer__year");
  $navLink = document.querySelector(".nav__link");
};

var prepareDOMEvents = function prepareDOMEvents() {
  var handleNav = function handleNav() {
    $navBtn.classList.toggle("is-active");
    $navMobile.classList.toggle("nav-mobile--active"); // $navMobile.forEach((item) => {
    //   item.addEventListener("click", () => {
    //     $navBtn.classList.remove("is--active");
    //   });
    // });
  };

  var handleCurrentYear = function handleCurrentYear() {
    var year = new Date().getFullYear();
    $footerYear.innerText = year;
  };

  handleCurrentYear();
  $navBtn.addEventListener("click", handleNav);
};

var main = function main() {
  prepareDOMElements();
  prepareDOMEvents();
};

document.addEventListener("DOMContentLoaded", main);